templates:
  {% macro render_text(text) %}
  {% if text is string %}
  {{text}}
  {% else %}
  {% if default_locale in text %}
  {{text[default_locale]}}
  {% elif "und" in text %}
  {{text['und']}}
  {% endif %}
  {% if check_alt_langs(text, default_locale) %}


  {% for a2l in locale_mapping %}
      {% if a2l in text and text[a2l] and (not a2l == "und") and (not a2l == default_locale) %}
      {{text[a2l]}}
      {% endif %}
  {% endfor %}


  {% endif %}
  {% endif %}
{% endmacro %}
{% macro render_resource(resource) %}

  {{ render_text(resource['url']) }}
  {% if resource['protocol'] %}
  {{ render_text(resource['protocol']['short_name']) }}
  {% endif %}
  {% if resource['app_profile'] %}
  {{ render_text(resource['app_profile']) }}
  {% endif %}
  {% if resource['name'] %}

    {{render_text(resource['name'])}}

  {% endif %}
  {% if resource['description'] %}

    {{render_text(resource['description'])}}

  {% endif %}
  {% if resource['function'] %}



  {% endif %}
  {% if resource['protocol_request'] %}
  {{ render_text(resource['protocol_request']) }}
  {% endif %}

{% endmacro %}

  {% macro render_distance(value, unit) %}
  {{value}}
  {% endmacro %}

  {% macro render_spatial_res(res) %}
    {% if res['scale'] %}



          {{res['scale']}}



    {% endif %}
    {% if res['distance'] %}


        {{ render_distance(res['distance'], res['distance_units']) }}


    {% endif %}
    {% if res['vertical'] %}


        {{ render_distance(res['vertical'], res['vertical_units']) }}


    {% endif %}
    {% if res['angular'] %}


        {{res['angular']}}


    {% endif %}
    {% if res['level_of_detail'] %}


        {{ render_text(res['level_of_detail']) }}


    {% endif %}
  {% endmacro %}
  {% macro render_time_period(start, end, is_ongoing) %}

    {{start.isoformat(timespec='milliseconds')}}
    {% if is_ongoing %}

    {% else %}
    {{end.isoformat(timespec='milliseconds')}}
    {% endif %}

  {% endmacro %}

  {% macro render_temporal_res(res) %}
  P{{res['years'] or 0}}Y{{res['months'] or 0}}M{{res['days'] or 0}}DT{{res['hours'] or 0}}H{{res['minutes'] or 0}}M{{res['seconds'] or 0}}S
  {% endmacro %}

{% macro render_responsibility(code, contact) %}





    {{ render_contact(contact) }}


{% endmacro %}

{% macro render_date(code, date) %}


    {{date.strftime("%Y-%m-%d")}}





{% endmacro %}

{% macro render_citation(citation) %}


    {{ render_text(citation['title']) }}

  {% if citation['alt_title'] %}
  {{ render_text(citation['alt_title']) }}
  {% endif %}
  {% if citation["publication_date"] %}
  {{ render_date('publication', citation['publication_date']) }}
  {% endif %}
  {% if citation["revision_date"] %}
  {{ render_date('revision', citation['revision_date']) }}
  {% endif %}
  {% if citation["creation_date"] %}
  {{ render_date('creation', citation['creation_date']) }}
  {% endif %}
  {% if citation['edition'] %}
  {{render_text(citation['edition'])}}
  {% endif %}
  {% if citation['edition_date'] %}
  {{citation['edition_date'].isoformat(timespec='milliseconds')}}
  {% endif %}
  {% if citation["id_code"] %}

    {{ render_id(citation['id_code'], citation['id_description'], citation['id_system']) }}

  {% endif %}
  {% if citation['responsibles'] %}
  {% for resp in citation['responsibles'] %}
  {{ render_responsibility(resp['role']['short_name'], resp['contact']) }}
  {% endfor %}
  {% endif %}
  {% if citation['presentation_form'] %}



  {% endif %}
  {#  #}
  {% if citation['details'] %}
  {{render_text(citation['details'])}}
  {% endif %}
  {% if citation['isbn'] %}
  {{ render_text(citation['isbn']) }}
  {% endif %}
  {% if citation['issn'] %}
  {{ render_text(citation['issn'])}}
  {% endif %}
  {% if citation['resource'] %}
  {{render_resource(citation['resource'])}}
  {% endif %}
  {#  #}

{% endmacro %}
{% macro render_locale(locale, with_id=False) %}
  {% if with_id %}

  {{ render_locale_details(locale) }}

  {% else %}

  {{ render_locale_details(locale) }}

  {% endif %}
{% endmacro %}
{% macro render_locale_details(locale) %}



  {% if locale['country'] %}



  {% endif %}
  {% if locale['encoding'] %}



  {% endif %}
{% endmacro %}
{% macro render_distribution(dc) %}

    {% if dc['description'] %}

      {{ render_text(dc['description']) }}

    {% endif %}
    {% if dc['responsibles'] %}
      {% for dcc in dc['responsibles'] %}



          {{ render_responsibility(dcc['role']['short_name'], dcc['contact']) }}



      {% endfor %}
    {% endif %}
    {% if dc['links'] %}



            {% for link in dc['links'] %}
              {{ render_resource(link) }}
            {% endfor %}



    {% endif %}

{% endmacro %}
{% macro render_ref_system(ref_system) %}


    {{ render_id(ref_system['code'], ref_system['description'], ref_system['id_system']) }}





{% endmacro %}
{% macro render_keywords(kwgroup) %}

  {% for keyword in kwgroup.keywords() %}

    {% if keyword.use_machine_key() %}
    {{keyword.machine_key}}
      {% if keyword.translations %}

        {% for key in keyword.translations %}

          {% if keyword.translations[key] and not key == "und" %}
        {{keyword.translations[key]}}
          {% endif %}

        {% endfor %}

      {% endif %}
    {% elif keyword.use_value_only() %}
      {{ render_text(keyword.machine_key) }}
    {% else %}
      {{ render_text(keyword.translations) }}
    {% endif %}

  {% endfor %}
  {% if kwgroup.thesaurus['type'] %}



  {% endif %}
  {% if kwgroup.thesaurus['citation'] %}

    {{ render_citation(kwgroup.thesaurus['citation']) }}

  {% endif %}

{% endmacro %}
{% macro render_maintenance_info(code, records) %}




  {% for record in records %}



        {{record['date'].strftime('%Y-%m-%d')}}






  {% endfor %}
  {#  #}
  {#  #}
  {% for record in records %}

    {{ render_text(record['notes']) }}

  {% endfor %}
  {#  #}

{% endmacro %}


{% macro render_constraint(con) %}
  {% if con['classification'] %}

      {{ render_constraints_content(con) }}



      {% if con['user_notes'] %}
      {{ render_text(con['user_notes']) }}
      {% endif %}
      {% if con['classification_system'] %}
      {{ render_text(con['classification_system']) }}
      {% endif %}
      {% if con['handling_description'] %}
      {{ render_text(con['handling_description']) }}
      {% endif %}

  {% elif con['access_constants'] or con['use_constraints'] or con['other_constraints'] %}

      {{ render_constraints_content(con) }}
      {% if con['access_constraints'] %}

        {% for c in con['access_constraints'] %}

        {% endfor %}

      {% endif %}
      {% if con['use_constraints'] %}

        {% for c in con['use_constraints'] %}

        {% endfor %}

      {% endif %}
      {% if con['other_constraints'] %}
      {{render_text(con['other_constraints'])}}
      {% endif %}

  {% else %}

      {{ render_constraints_content(con) }}

  {% endif %}
{% endmacro %}

  {% macro render_constraints_content(con) %}
    {% if con['description'] %}
      {{ render_text(con['description']) }}
    {% endif %}
    {% if con['reference'] %}
      {% for citation in con['reference'] %}
      {{ render_citation(citation) }}
      {% endfor %}
    {% endif %}
    {% if con['graphic'] %}
      {% for graphic in con['graphic'] %}
      {{ render_graphic(graphic)}}
      {% endfor %}
    {% endif %}
  {% if con['app_scope'] %}
    {{ render_scope(con['app_scope']) }}
  {% endif %}
  {% if con['responsibles'] %}
    {% for party in con['responsibles'] %}
    {{ render_responsibility(party['role']['short_name'], party['contact']) }}
    {% endfor %}
  {% endif %}
  {% if con['releasability'] %}

      {{ render_releasability(con['releasability']) }}

  {% endif %}
  {% endmacro %}

  {% macro render_scope(scope) %}





  {% endmacro %}

  {% macro render_graphic(graphic) %}

      {{graphic['file_url']}}
      {% if graphic['description'] %}
      {{ render_text(graphic['description']) }}
      {% endif %}
      {% if graphic['file_ext'] %}
      {{graphic['file_ext']}}
      {% endif %}
      {% if graphic['use_constraint'] %}

        {% for con in graphic['use_constraint'] %}
        {{ render_constraint(con) }}
        {% endfor %}

      {% endif %}
      {% if graphic['reference_link'] %}
          {% for link in graphic['reference_link'] %}

          {{ render_resource(link) }}

          {% endfor %}
      {% endif %}

  {% endmacro %}

  {% macro render_releasability(r) %}

    {% if r['statement'] %}
      {{ render_text(r['statement']) }}
    {% endif %}
    {% if r['addressees'] %}
      {% for addr in r['addressees'] %}

      {{ render_responsibility(addr['type']['short_name'], addr['contact']) }}

      {% endfor %}
    {% endif %}
    {% if r['constraints'] %}

      {% for con in r['constraints'] %}

      {% endfor %}

    {% endif %}

  {% endmacro %}

  {% macro render_contact_content(contact) %}
  {% if contact['organization_name'] %}
  {{render_text(contact['organization_name'])}}
  {% elif contact['individual_name'] %}
    {{render_text(contact['individual_name'])}}
  {% endif %}
  {% if contact['phone'] or contact['web_resource'] or contact['service_hours'] or contact['instructions'] or contact['delivery_point'] or contact['city'] or contact['admin_area'] or contact['postal_code'] or contact['country'] or contact['email'] %}


      {% if contact['phone'] %}



            {{contact['phone']}}



      {% endif %}
      {% if contact['delivery_point'] or contact['city'] or contact['admin_area'] or contact['postal_code'] or contact['country'] or contact['email'] %}


          {% if contact['delivery_point'] %}

          {% for dp in contact['delivery_point'] %}
            {{dp}}
          {% endfor %}

          {% endif %}
          {% if contact['city'] %}

            {{contact['city']}}

          {% endif %}
          {% if contact['admin_area'] %}

            {{contact['admin_area']}}

          {% endif %}
          {% if contact['postal_code'] %}

            {{contact['postal_code']}}

          {% endif %}
          {% if contact['country'] %}

            {{contact['country']}}

          {% endif %}
          {% if contact['email'] %}

            {{contact['email']}}

          {% endif %}


      {% endif %}
      {% if contact['web_resource'] %}
        {% for res in contact['web_resource'] %}

        {{render_resource(res)}}

        {% endfor %}
      {% endif %}
      {% if contact['service_hours'] %}

        {{render_text(contact['service_hours'])}}

      {% endif %}
      {% if contact['instructions'] %}

        {{render_text(contact['instructions'])}}

      {% endif %}


  {% endif %}
  {% if contact['id_code'] %}

    {{ render_id(contact['id_code'], contact['id_description'], contact['id_system']) }}

  {% endif %}
  {% if contact['logo'] %}
  {{ render_graphic(contact['logo']) }}
  {% endif %}
  {% if contact['position_name'] and not contact['organization_name'] %}
    {{render_text(contact['position_name'])}}
    {% endif %}
  {% if contact['individuals'] %}
  {% for c in contact['individuals'] %}
  {{render_contact(c)}}
  {% endfor %}
  {% endif %}
{% endmacro %}
{% macro render_contact(contact) %}
  {% if contact['organization_name'] or contact['logo'] %}

      {{ render_contact_content(contact) }}

  {% else %}

      {{ render_contact_content(contact) }}

  {% endif %}
{% endmacro %}
  {% macro render_id(code, desc, system) %}

    {% if system['authority'] %}

    {{ render_citation(system['authority']) }}

    {% endif %}
    {{code}}
    {% if system['code_space'] %}
    {{system['code_space']}}
    {% endif %}
    {% if system['version'] %}
    {{system['version']}}:
    {% endif %}
    {% if desc %}
    {{ render_text(desc) }}
    {% endif %}

  {% endmacro %}
